INSERT IGNORE INTO `nonalcohol` VALUES ('5', 'Shirley Temples', 'A Shirley Temple is a non-alcoholic mixed drink traditionally made with ginger ale and a splash of grenadine, garnished with a maraschino cherry. Modern Shirley Temple recipes may substitute lemon-lime soda or lemonade and sometimes orange juice in part, or in whole.', '#NAME?', '24', '18', '22', '4.3', 'https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/20191115-shirley-temple-delish-ehg-4460-1585335880.jpg?crop=1xw:1xh;center,top&resize=980:*', '24000') ON DUPLICATE KEY UPDATE `no` = VALUES(`no`), `name` = VALUES(`name`), `exp` = VALUES(`exp`), `origin` = VALUES(`origin`), `hashtag1` = VALUES(`hashtag1`), `hashtag2` = VALUES(`hashtag2`), `hashtag3` = VALUES(`hashtag3`), `score` = VALUES(`score`), `img` = VALUES(`img`), `price` = VALUES(`price`)
INSERT IGNORE INTO `nonalcohol` VALUES ('7', 'Blackberry Virgin Mojito', 'Classic mojitos are always refreshing with fresh mint and fruit. This non-alcoholic version is still every bit as refreshing with an easy mint simple syrup and fresh blackberries. It will have you dreaming of care free summer days. The simple syrup will make more than you need, but you can keep it refrigerated for 1 week and save it for another use like in homemade lemonade.', '#NAME?', '23', '21', '24', '3.7', 'https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/20191120-blackberry-virgin-mojito-delish-ehg-4728-1583519572.jpg?crop=1xw:1xh;center,top&resize=980:*', '6000') ON DUPLICATE KEY UPDATE `no` = VALUES(`no`), `name` = VALUES(`name`), `exp` = VALUES(`exp`), `origin` = VALUES(`origin`), `hashtag1` = VALUES(`hashtag1`), `hashtag2` = VALUES(`hashtag2`), `hashtag3` = VALUES(`hashtag3`), `score` = VALUES(`score`), `img` = VALUES(`img`), `price` = VALUES(`price`)
