INSERT IGNORE INTO `nonalcohol` VALUES ('10', 'Frosted Lemonade', 'Mix lemon juice and sugar together in a pitcher until the sugar is fully dissolved. Add water to dilute and chill. Into a blender, add lemonade and ice cream. Blend until smooth and divide among 3 cups. Garnish with slices of lemon and serve.', '#NAME?', '11', '20', '6', '2.9', 'https://hips.hearstapps.com/del.h-cdn.co/assets/17/28/640x958/gallery-1499795330-delish-copycat-chik-fil-a-frozen-lemonade-pinterest-3.jpg?resize=980:*', '3000') ON DUPLICATE KEY UPDATE `no` = VALUES(`no`), `name` = VALUES(`name`), `exp` = VALUES(`exp`), `origin` = VALUES(`origin`), `hashtag1` = VALUES(`hashtag1`), `hashtag2` = VALUES(`hashtag2`), `hashtag3` = VALUES(`hashtag3`), `score` = VALUES(`score`), `img` = VALUES(`img`), `price` = VALUES(`price`)
