INSERT INTO `soju` VALUES ('2','4', 'Ije-Urin', 'Ije-Urin made in Shenyang is said to be soju with 3 times more oxygen. It has a slogan that relieves hangovers 30 minutes faster after drinking soju because of the high amount of oxygen. Soju sold in the Chungnam region.', 'Korea', '10', '17', '7', '4.6', 'https://img3.yna.co.kr/etc/inner/KR/2018/10/30/AKR20181030117000063_02_i_P4.jpg', '1700');
INSERT INTO `soju` VALUES ('2','5', 'Cool Cheongpung', 'It is \"Cool Cheongpung\" that is made in Chungcheongbuk-do and sells soju with the title of \"Water taste.\"', 'Korea', '4', '3', '21', '4.8', 'https://img6.yna.co.kr/etc/inner/KR/2019/05/29/AKR20190529160100064_01_i_P2.jpg', '1600');
INSERT INTO `soju` VALUES ('2','6', 'Hite', 'It is soju made in Jeollabuk-do and is clear and transparent. 100% ionized water is used', 'Korea', '16', '23', '22', '4.5', 'https://t1.daumcdn.net/cfile/tistory/993B87505A6811422A', '1900');
INSERT INTO `soju` VALUES ('2','7', 'Ipsaeju', 'Soju is eaten in Jeollanam-do. Because of the high content of Canada\'s pure maple tree sap, it is said to be soju that gets sweeter the more you shake it.', 'Korea', '15', '20', '3', '4', 'https://t1.daumcdn.net/cfile/tistory/9992EA425A68113D1A', '1800');
INSERT INTO `soju` VALUES ('2','8', 'Daeseon', 'Daeseon is Busan\'s representative soju. The distilled soju juice that has been aged for a long time was added to add a clean and deep taste.', 'Korea', '6', '8', '30', '4.2', 'https://lh3.googleusercontent.com/proxy/g-3I-umeycB7F8ZlkuoZ0YERTWxSY04kF5YBlPRsUDstu7b25xQhVYgJTSMcFyQ1X_LZaGI_ST6BGSu5xmgnSrdic9Qra11hvJMfeyFeVqNqYs1LueA1NFh0czv7K-rG60nCsrTKAQ03s0ruFdJUy1swaQJ1ACJOs3BBCw', '2100');
INSERT INTO `soju` VALUES ('2','9', 'Delicious cham', 'It is made in Gyeongsangbuk-do, and is soft and smooth. In particular, it catches the unpleasant smell of soju, so it is smooth for drinking. ', 'Korea', '15', '26', '25', '3.8', 'https://www.intn.co.kr/news/photo/201907/2006594_668353_2920.png', '2000');
INSERT INTO `soju` VALUES ('2','10', 'Good day', 'It is most popular in Busan, Gyeongnam and Ulsan. Fruit soju, a leading contributor to rising awareness, has pomegranate, grapefruit, citron, blueberry, peach, and pineapple flavors.', 'Korea', '4', '5', '28', '3.4', 'http://www.busan.com/nas/wcms/wcms_data/photos/2019/11/25/2019112519391183776_l.jpg', '1800');
INSERT INTO `soju` VALUES ('2','11', 'Jinro is Back', 'The\'Jinro is Back\' is an attractive retro transparent bottle that stimulates retro sensibility.\nThe advertising model is toad. It is slightly sweeter than other soju.', 'Korea', '12', '13', '14', '3.6', 'https://image.chosun.com/sitedata/image/201910/01/2019100102166_0.png', '2300');
INSERT INTO `soju` VALUES ('2','12', 'Oak barrel clear dew', 'This soju is characterized by blending the distilled liquor of fermented rice, aged for more than 3 years in an oak barrel with alcohol based soju, to emphasize the subdued flavor and soft finish of the wooden barrel.', 'Korea', '11', '5', '3', '3.8', 'https://img2.yna.co.kr/etc/inner/KR/2017/12/07/AKR20171207047000030_01_i_P2.jpg', '2000');
INSERT INTO `soju` VALUES ('2','13', 'Hwayo', 'Five types are available on the market, each with 17, 25, 41 and 53 degrees. The scent is not strong and it is soft and not burdensome compared to the degree. Above 41 degrees, tonic water is added and eaten as a highball.', 'Korea', '8', '1', '2', '3.6', 'https://m.kanashop.kr/web/product/big/201902/59d0e3db7b9cbaccf292eb4ef30fda4a.jpg', '15000');
INSERT INTO `soju` VALUES ('2','14', 'Andong Soju', 'The longer it is stored, the better the flavor. Even though Andong Soju is a high-level liquor at 45°, the fragrance gently spreads in the mouth, refreshing the mouth, and there is no hangover after drinking.', 'Korea', '9', '7', '29', '3.7', 'https://img.danawa.com/prod_img/500000/740/291/img/5291740_1.jpg?shrink=500:500&_v=20180830142133', '14000');
